          currentReport["–°—Ç–∞—Ç—É—Å"] = statusValue;
          if (statusValue.toLowerCase().match(/–≤—ã–ø–æ–ª–Ω(–µ–Ω|–∏–ª|–µ–Ω–æ|–∏–ª–∞|–∏–ª–∏)/) && !statusValue.toLowerCase().includes('–Ω–µ')) {
            currentReport["–ü—Ä–æ—Ü–µ–Ω—Ç"] = 100;
          } else if (statusValue.toLowerCase().includes('—á–∞—Å—Ç–∏—á–Ω–æ')) {
            currentReport["–ü—Ä–æ—Ü–µ–Ω—Ç"] = 50;
          } else if (statusValue.toLowerCase().match(/–Ω–µ\s*–≤—ã–ø–æ–ª–Ω/)) {
