          currentReport["–ü—Ä–æ—Ü–µ–Ω—Ç"] = parseInt(percentValue.replace('%', '')) || 0;
          currentField = null;
        }
        else if (lowerLine.includes('–∫–æ–º–º–µ–Ω—Ç–∞—Ä–∏–π:')) {
          currentReport["–ö–æ–º–º–µ–Ω—Ç–∞—Ä–∏–π"] = line.substring(line.toLowerCase().indexOf('–∫–æ–º–º–µ–Ω—Ç–∞—Ä–∏–π:') + 12).trim();
