// –û–±—Ä–∞–±–æ—Ç–∫–∞ –æ—Ç—á–µ—Ç–æ–≤ –∏–∑ action process_report
const data = $input.first().json;
if (data.action === 'process_report' && data.reports) {
  return data.reports.map(report => ({ json: report }));
}

// –ò–Ω–∞—á–µ –ø–µ—Ä–µ–¥–∞–µ–º –¥–∞–Ω–Ω—ã–µ –∫–∞–∫ –µ—Å—Ç—å –¥–ª—è —Å–æ–≤–º–µ—Å—Ç–∏–º–æ—Å—Ç–∏
return $input.all();

// –ì—Ä—É–ø–ø–∏—Ä—É–µ–º –æ—Ç—á–µ—Ç—ã –ø–æ –∏—Å–ø–æ–ª–Ω–∏—Ç–µ–ª—é
const reports = $input.all();
const reportsByForeman = {};

reports.forEach(item => {
  const report = item.json;
  const foremanId = report.ID_—á–∞—Ç–∞ || report["ID_—á–∞—Ç–∞"];

  if (!reportsByForeman[foremanId]) {
