function safeString(value) {
  return String(value || '').trim();
}

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–∞—Ä—Å–∏–Ω–≥–∞ –¥–∞—Ç—ã –æ—Ç–ø—Ä–∞–≤–∫–∏
function parseSentDate(sentDateStr) {
  sentDateStr = safeString(sentDateStr);
  if (!sentDateStr) return null;
  try {
    const [datePart, timePart] = sentDateStr.split(', ');
    const [day, month, year] = datePart.split('.');
    const [hours, minutes] = timePart.split(':');
    return new Date(parseInt(year), parseInt(month) - 1, parseInt(day), parseInt(hours), parseInt(minutes));
  } catch (e) {
    console.error('–û—à–∏–±–∫–∞ –ø–∞—Ä—Å–∏–Ω–≥–∞ –¥–∞—Ç—ã –æ—Ç–ø—Ä–∞–≤–∫–∏:', sentDateStr, e);
