}

return messages;

// –ò–∑–≤–ª–µ–∫–∞–µ–º –¥–∞–Ω–Ω—ã–µ –¥–ª—è –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è –∏–∑ –≤—Å–µ—Ö —Å–æ–æ–±—â–µ–Ω–∏–π
const allMessages = $input.all();

// –¢–µ–∫—É—â–µ–µ –≤—Ä–µ–º—è
const now = new Date();
const sentDateTime = now.toLocaleString('ru-RU', {
  day: '2-digit',
  month: '2-digit',
  year: 'numeric',
  hour: '2-digit',
  minute: '2-digit'
});

// –°–æ–∑–¥–∞–µ–º –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è –¥–ª—è –∫–∞–∂–¥–æ–π —Å—Ç—Ä–æ–∫–∏
const updates = [];
const processedRows = new Set();

// –û–±—Ä–∞–±–∞—Ç—ã–≤–∞–µ–º –í–°–ï —Å–æ–æ–±—â–µ–Ω–∏—è - –∏ –ø—Ä–∏–≤–µ—Ç—Å—Ç–≤–µ–Ω–Ω—ã–µ, –∏ –ø–æ–≤—Ç–æ—Ä–Ω—ã–µ
allMessages.forEach(msg => {
  // –î–ª—è –ø—Ä–∏–≤–µ—Ç—Å—Ç–≤–µ–Ω–Ω—ã—Ö —Å–æ–æ–±—â–µ–Ω–∏–π
  if (msg.json.isGreeting === true && msg.json.rowIndexes) {
    const rowIndexes = msg.json.rowIndexes || [];
    rowIndexes.forEach(rowIndex => {
      if (rowIndex && !isNaN(rowIndex) && !processedRows.has(rowIndex)) {
        processedRows.add(rowIndex);
        updates.push({
          json: {
            values: [["–î–∞", sentDateTime, "1"]], // –ù–∞—á–∞–ª—å–Ω—ã–π —Å—á–µ—Ç—á–∏–∫ = 1
            _rowIndex: Number(rowIndex)
          }
        });
      }
    });
  }

  // –î–ª—è –ø–æ–≤—Ç–æ—Ä–Ω—ã—Ö –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏–π
  if (msg.json.isReminder === true && msg.json.rowIndex) {
    const rowIndex = msg.json.rowIndex;
    const count = msg.json.count || 1;
    if (rowIndex && !isNaN(rowIndex) && !processedRows.has(rowIndex)) {
      processedRows.add(rowIndex);
      updates.push({
        json: {
          values: [["–î–∞", sentDateTime, String(count)]],
          _rowIndex: Number(rowIndex)
        }
      });
    }
  }
});

console.log(`–ü–æ–¥–≥–æ—Ç–æ–≤–ª–µ–Ω–æ ${updates.length} –æ–±–Ω–æ–≤–ª–µ–Ω–∏–π`);
return updates.length > 0 ? updates : [];

// –ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞ –¥–∞–Ω–Ω—ã—Ö –¥–ª—è –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è —Å—É—â–µ—Å—Ç–≤—É—é—â–µ–π –∑–∞–¥–∞—á–∏
const input = $json;

// –ü—Ä–æ–≤–µ—Ä–∫–∞ –Ω–∞–ª–∏—á–∏—è –¥–∞–Ω–Ω—ã—Ö
if (!input || !input.lookup || !input.filesGroup) {
  console.error('–û—Ç—Å—É—Ç—Å—Ç–≤—É—é—Ç –Ω–µ–æ–±—Ö–æ–¥–∏–º—ã–µ –¥–∞–Ω–Ω—ã–µ');
  return [];
}

const lookupResult = input.lookup;
const filesGroup = input.filesGroup;

if (lookupResult.length === 0) {
  console.error('–ù–µ –Ω–∞–π–¥–µ–Ω–∞ —Å—É—â–µ—Å—Ç–≤—É—é—â–∞—è –∑–∞–ø–∏—Å—å');
  return [];
}

// –ë–µ—Ä–µ–º —Å—É—â–µ—Å—Ç–≤—É—é—â—É—é –∑–∞–ø–∏—Å—å
const existing = lookupResult[0];
const rowNumber = existing._rowNumber || 2;

// –°–æ–±–∏—Ä–∞–µ–º –¥–∞–Ω–Ω—ã–µ —Å–æ –≤—Å–µ—Ö –Ω–æ–≤—ã—Ö —Ñ–∞–π–ª–æ–≤
const newFileNames = [];
const newFileLinks = [];
const newFileIds = [];
let newTotalSize = 0;
const newCaptions = [];
const newMediaGroupIds = new Set();

// –ü—Ä–æ—Ö–æ–¥–∏–º –ø–æ –≤—Å–µ–º –Ω–æ–≤—ã–º —Ñ–∞–π–ª–∞–º
filesGroup.forEach(file => {
  if (file && file.fileData) {
    // –î–æ–±–∞–≤–ª—è–µ–º –∏–º—è —Ñ–∞–π–ª–∞
    if (file.fileData.fileName) {
      newFileNames.push(file.fileData.fileName);
    }

    // –î–æ–±–∞–≤–ª—è–µ–º —Å—Å—ã–ª–∫—É –Ω–∞ —Ñ–∞–π–ª
    if (file.fileData.fileLink) {
      newFileLinks.push(file.fileData.fileLink);
    }

    // –î–æ–±–∞–≤–ª—è–µ–º Telegram File ID
    if (file.fileData.fileId) {
      newFileIds.push(file.fileData.fileId);
    }

    // –°—É–º–º–∏—Ä—É–µ–º —Ä–∞–∑–º–µ—Ä
    newTotalSize += file.fileData.fileSize || 0;

    // –°–æ–±–∏—Ä–∞–µ–º –∫–æ–º–º–µ–Ω—Ç–∞—Ä–∏–∏
    if (file.reportData?.caption) {
      newCaptions.push(file.reportData.caption);
    }

    // –°–æ–±–∏—Ä–∞–µ–º Media Group IDs
    if (file.messageData?.mediaGroupId) {
      newMediaGroupIds.add(file.messageData.mediaGroupId);
    }
  }
});

// –ü–∞—Ä—Å–∏–º —Å—É—â–µ—Å—Ç–≤—É—é—â–∏–µ –∑–Ω–∞—á–µ–Ω–∏—è
const currentCount = parseInt(existing['–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ñ–∞–π–ª–æ–≤']) || 0;
const newFilesCount = newFileNames.length;
const totalCount = currentCount + newFilesCount;

// –û–±—ä–µ–¥–∏–Ω—è–µ–º —Å—É—â–µ—Å—Ç–≤—É—é—â–∏–µ –∏ –Ω–æ–≤—ã–µ –¥–∞–Ω–Ω—ã–µ
const allFileNames = existing['–ò–º—è —Ñ–∞–π–ª–∞'] ? 
  existing['–ò–º—è —Ñ–∞–π–ª–∞'] + ', ' + newFileNames.join(', ') : 
  newFileNames.join(', ');

const allFileLinks = existing['–°—Å—ã–ª–∫–∞ –Ω–∞ —Ñ–∞–π–ª'] ? 
  existing['–°—Å—ã–ª–∫–∞ –Ω–∞ —Ñ–∞–π–ª'] + ', ' + newFileLinks.join(', ') : 
  newFileLinks.join(', ');

const allFileIds = existing['Telegram File ID'] ? 
  existing['Telegram File ID'] + ', ' + newFileIds.join(', ') : 
  newFileIds.join(', ');

const totalSize = (parseInt(existing['–†–∞–∑–º–µ—Ä —Ñ–∞–π–ª–∞ (–±–∞–π—Ç)']) || 0) + newTotalSize;

// –û–±–Ω–æ–≤–ª—è–µ–º —Å—Ç–∞—Ç—É—Å
const newStatus = totalCount === 1 ? 
  '–§–∞–π–ª –∑–∞–≥—Ä—É–∂–µ–Ω –≤ Drive' : 
  `–í—Å–µ ${totalCount} —Ñ–∞–π–ª–∞(–æ–≤) –∑–∞–≥—Ä—É–∂–µ–Ω—ã –≤ Drive`;

// –û–±–Ω–æ–≤–ª—è–µ–º –∫–æ–º–º–µ–Ω—Ç–∞—Ä–∏–π
const existingComment = existing['–ö–æ–º–º–µ–Ω—Ç–∞—Ä–∏–π'] || '';
const uniqueNewCaptions = [...new Set(newCaptions)];
const newComment = uniqueNewCaptions.length > 0 ? 
  (existingComment ? existingComment + '; ' + uniqueNewCaptions.join('; ') : uniqueNewCaptions.join('; ')) : 
  existingComment;

// –û–±–Ω–æ–≤–ª—è–µ–º Media Group IDs
const existingMediaGroups = existing['Media Group ID'] ? existing['Media Group ID'].split(', ') : [];
const allMediaGroups = new Set([...existingMediaGroups, ...newMediaGroupIds]);
const mediaGroupIdsStr = Array.from(allMediaGroups).join(', ');

// –ë–µ—Ä–µ–º –¥–∞–Ω–Ω—ã–µ –æ—Ç—á–µ—Ç–∞
const reportData = input.reportData;

console.log(`–û–±–Ω–æ–≤–ª–µ–Ω–∏–µ –∑–∞–ø–∏—Å–∏ –¥–ª—è ID_–∑–∞–¥–∞—á–∏ ${existing['ID_–æ—Ç—á—ë—Ç–∞']}:`, {
  existingFiles: currentCount,
  newFiles: newFilesCount,
  totalFiles: totalCount
});

// –í–æ–∑–≤—Ä–∞—â–∞–µ–º –¥–∞–Ω–Ω—ã–µ –¥–ª—è –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è
return [{
  json: {
    rowNumber: rowNumber,
    updateData: {
      '–ò–º—è —Ñ–∞–π–ª–∞': allFileNames,
      '–°—Å—ã–ª–∫–∞ –Ω–∞ —Ñ–∞–π–ª': allFileLinks,
      'Telegram File ID': allFileIds,
      '–†–∞–∑–º–µ—Ä —Ñ–∞–π–ª–∞ (–±–∞–π—Ç)': String(totalSize),
      '–°—Ç–∞—Ç—É—Å –æ–±—Ä–∞–±–æ—Ç–∫–∏': newStatus,
      '–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ñ–∞–π–ª–æ–≤': String(totalCount),
      '–ö–æ–º–º–µ–Ω—Ç–∞—Ä–∏–π': newComment,
      'Media Group ID': mediaGroupIdsStr,
      '–í—Ä–µ–º—è –ø–æ–ª—É—á–µ–Ω–∏—è –æ—Ç—á—ë—Ç': new Date().toLocaleString('ru-RU')
    },
    // –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –¥–ª—è –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏—è
    confirmationData: {
      '_chatId': reportData.chatId,
      '_taskId': existing['ID_–æ—Ç—á—ë—Ç–∞'],
      '_originalMessageId': reportData.originalMessageId,
      '_filesCount': totalCount,
      '_newFilesCount': newFilesCount,
      '_fileNames': allFileNames,
      'ID_–æ—Ç—á—ë—Ç–∞': existing['ID_–æ—Ç—á—ë—Ç–∞'],
      '–î–∞—Ç–∞ –æ—Ç—á—ë—Ç–∞': existing['–î–∞—Ç–∞ –æ—Ç—á—ë—Ç–∞'],
      '–í—Ä–µ–º—è –ø–æ–ª—É—á–µ–Ω–∏—è –æ—Ç—á—ë—Ç': new Date().toLocaleString('ru-RU'),
      '–°—Ç–∞—Ç—É—Å –æ–±—Ä–∞–±–æ—Ç–∫–∏': newStatus
    }
  }
}];
